body.script:
		var storage = function(key, value) {
			if (typeof value === 'undefined') {
				return localStorage.getItem(key);
			}
			return localStorage.setItem(key, value);
		};

		var format = function(template, data) {
			return template.replace(/\{\{\s*(.*?)\s*?\}\}/g, function($0, $1) {
				return data[$1] || '';
			});
		};

		var getUrlParamValue = function(name) {
			if (name == null || name == 'undefined') {
				return null;
			}

			var searchStr = decodeURI(location.search);

			var infoIndex = searchStr.indexOf(name + "=");

			if (infoIndex == -1) {
				return null;
			}

			var searchInfo = searchStr.substring(infoIndex + name.length + 1);

			var tagIndex = searchInfo.indexOf("&");

			if (tagIndex != -1) {
				searchInfo = searchInfo.substring(0, tagIndex);
			}

			return searchInfo;
		}

		var tokenType = storage('token_type');
		var survivalTime = storage('survival_time');
		var accessToken = storage('access-token');
		var userName = storage('userName');

		// 登录成功
		if (!((!tokenType || !accessToken) || (((+new Date) / 1000) | 0) > parseInt(survivalTime))) {
			// $('.head .sign').html('\
			// \
			// 	Welcome, ' + userName + '\
			// ');
			$('.head .sign').html('\
			\
				My Account\
			');
			$('.head_wap .waplga').html('Welcome, ' + userName + '');
		}

		var code = getUrlParamValue('referral_code');

		if (code) {
			var nowTime = Math.ceil(Date.now() / 100);
			window.localStorage.setItem("referral_code", code, (nowTime + (4 * 60 * 60)));
		}

		$(function() {
			$('.whead i,.wapnavtop i').click(function() {
				$('.wapnav').toggleClass('wapnav_active');
				$('.wpnvbg').toggleClass('wpnvbg_active');
			});

			$('.wpnvbg').click(function() {
				$('.wapnav').removeClass('wapnav_active');
				$('.wpnvbg').removeClass('wpnvbg_active');
			});

			$('.wpheadhide a').click(function() {
				$('.wpnvbg').click();
			});

			$('.wapprda').click(function() {
				$(this).siblings('.wpheadhide').slideToggle();
				$(this).toggleClass('wapprda_active');
			});

			// 设置路由地址
			function setNavAactive() {
				var fileName = location.pathname.match(/([A-z]*(\.html)?)$/)[1];
				var navLink = $('.navigation a, .head_wap a');
				navLink.each(function() {
					$(this).parent().removeClass('active');
				});
				var activeNav = navLink.filter(function() {
					if (!fileName) return;
					return $(this).attr('href').indexOf(fileName) > -1;
				});
				const box = activeNav.parent();
				if (box.hasClass('sub-menu-item')) {
					box.parents('span').addClass('active');
					activeNav.addClass('active');
				} else {
					box.addClass('active');
				}

				if (box.hasClass('wpheadhide')) {
					box.addClass('wapprda_active');
					box.show();
				}
			}

			setNavAactive();
		});
		$(document).ready(function() {
			// 添加点击事件
			$('.black-box').click(function() {
				window.location.href = '/aapanel_x_clawcloud';
			});
		});

		// 获取活动信息--下面是黑五活动下架时删除
		$(document).ready(function() {
			var activity_id = 10; // 活动id
			var isActivity = true; // 活动是否已开启
			var server_time = null; // 当前服务器时间
			var end_timer = null; // 倒计时时间

			// 获取活动信息
			get_activity_info();

			function get_activity_info() {
				$.ajax({
					url: '/api/activity/' + activity_id,
					type: 'GET',
					contentType: 'application/json',
					success: function(datas) {
						server_time = datas.nonce;
						if (!datas.res.is_finished) {
							$('.black-box').css({
								'display': 'flex',
							});
							setCookies({
								isFinish: false
							}, 7)
						}
						if (datas.nonce < datas.res.start_time) {
							isActivity = false; // 活动是否开始
							startCountdown(datas.nonce, datas.res.start_time);
						} else {
							isActivity = true;
							startCountdown(datas.nonce, datas.res.end_time);
						}
					},
					error: function(xhr) {
						console.error('Request failed with status:', xhr.status);
					}
				});
			}
			// 设置cookie存储
			function setCookies(obj, limitTime) {
				let data = new Date(new Date().getTime() + limitTime * 24 * 60 * 60 * 1000).toUTCString()
				for (let i in obj) {
					document.cookie = i + '=' + obj[i] + ';expires=' + data
				}

			}

			function startCountdown(serverTime, endTime) {
				var dateBox = $('#date_box1');

				// 将时间戳转换为 Date 对象
				var serverDate = new Date(serverTime * 1000); // 将服务器时间转换为毫秒
				var endDate = new Date(endTime * 1000); // 将结束时间转换为毫秒

				function updateCountdown() {
					var now = new Date(); // 当前时间
					var timeStamp = endDate - now; // 计算剩余的毫秒数

					if (timeStamp > 0) {
						var days = Math.floor(timeStamp / (1000 * 60 * 60 * 24));
						var hours = Math.floor((timeStamp % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
						var minutes = Math.floor((timeStamp % (1000 * 60 * 60)) / (1000 * 60));
						var seconds = Math.floor((timeStamp % (1000 * 60)) / 1000);

						// 格式化时间
						var _days = String(days).padStart(2, '0');
						var _hours = String(hours).padStart(2, '0');
						var _minutes = String(minutes).padStart(2, '0');
						var _seconds = String(seconds).padStart(2, '0');

						// 更新页面内容
						dateBox.html(`

${_days}Days

${_hours}Hour

${_minutes}Minutes

${_seconds}Sec
		                    `);
					} else {
						clearInterval(countdownInterval);
					}
				}

				// 每秒更新一次
				var countdownInterval = setInterval(updateCountdown, 1000);
				updateCountdown(); // 初始调用一次以显示倒计时
			}

		})

