		});


		window.addEventListener("scroll", function(e) {
			// 当发生滚动事件时调用ergodic事件
			ergodic();
		});

		function ergodic() {
			var images = document.getElementsByTagName("img");
			// 遍历每一张图
			for (var i of images) {
				// 判断当前图片是否在可视区内
				if (i.getBoundingClientRect().top < window.innerHeight) {
					// 获取自定义data-src属性的值
					var trueSrc = i.getAttribute("data-src");
					var imgSrc = i.getAttribute("src");
					// 把值赋值给图片的src属性
					if (!imgSrc) i.setAttribute("src", trueSrc);
				}
			}
		}
		ergodic();
	});
